  useEffect(() => {
    const api = 'http://localhost:9001/products';
    fetch(api)
      .then(result => result.json())
      .then((result) => {
        setProducts(result.data);
        setLoading(false);
      })
      .catch(error => {
        console.error('Ошибка загрузки данных:', error);
        setLoading(false);
      });
  }, []);

  return (
    <div className="Main">
      {loading ? <p>Загрузка...</p> : Array.isArray(products) && products.length > 0 ? (
        products.map((item) => (
          <Product key={item.id} images={images} title={item.title} price={item.price} />
        ))
      ) : (
        <p>Нет продуктов для отображения</p>
      )}
    </div>
  );
}
