import React, {useState, useEffect} from 'react';
import './Main.css';
import Product from '../components/Product';
import images from '../images/acrap.jpg';

function Main() {

  const [products, setProducts] = useState([]);
  
  useEffect(() => {

    const api = 'http://localhost:9001/products';

    // fetch(api)
    // .then(result => result.json())
    // .then((result) => {
    //   console.log(result);
    //   setProducts(result.data)
    // })
    fetch(api)
     .then(result => {
       if (!result.ok) {
         throw new Error('Network response was not ok');
       }
       return result.json();
     })
     .then((result) => {
       console.log(result);
       setProducts(result.data); // убедитесь, что result.data - это массив
     })
     .catch((error) => {
       console.error('Ошибка:', error);
     });
  }, [])

  return (
    <div className="Main">
      {Array.isArray(products) ? (
        products.map((item) => (
          <Product key={item.id} images={images} title={item.title} price={item.price} />
        ))
      ) : (
        <p>Нет продуктов для отображения</p>
      )}
    </div>
  );
}
export default Main;

