import React, {useState, useEffect} from 'react';
import './Main.css';
import Product from '../components/Product';
import images from '../images/acrap.jpg';


function Main() {
   
  const [products, setProducts] = useState([]);
  
  fetch('http://localhost:9001/products')
  .then(response => {
      if (!response.ok) {
          throw new Error('Network response was not ok ' + response.statusText);
      }
      return response.json();
  })
  .then(data => {
      console.log(data.message); // Это должно выводить массив продуктов
      // Дальше отобразите данные в вашем интерфейсе
  })
  .catch(error => {
      console.error('There has been a problem with your fetch operation:', error);
  });
  const renderProducts = (products) => {
    const container = document.getElementById('product-container');
    container.innerHTML = ''; // Очистите контейнер перед добавлением новых продуктов
    products.forEach(product => {
        const productElement = document.createElement('div');
        productElement.textContent = product.name; // Или любое другое поле из объекта
        container.appendChild(productElement);
    });
};
  // useEffect(() => {

  //   const api = 'http://localhost:9001/products';

  //   fetch(api)
  //   .then(result => result.json())
  //   .then((result) => {
  //     console.log(result);
  //     setProducts(result.data)
  //   })
  // }, [])

  return (
    <div className="Main">
      {Array.isArray(products) ? (
        products.map((item) => (
          <Product key={item.id} images={images} title={item.title} price={item.price} />
        ))
      ) : (
        <p>Нет продуктов для отображения</p>
      )}
    </div>
  );

}
export default Main;


