import React, { useState, useEffect } from 'react';
import './Main.css';
import Product from '../components/Product';
import images from '../images/acrap.jpg';

function Main() {
    const [products, setProducts] = useState([]);

    useEffect(() => {
        const api = 'http://localhost:9001/products';

        fetch(api)
            .then(result => result.json())
            .then((result) => {
                console.log(result);
                setProducts(result.data);
            })
            .catch(error => console.error('Ошибка:', error)); // Обработка ошибок
    }, []);

    return (
        <div className="Main">
            {Array.isArray(products) && products.length > 0 ? (
                products.map((item) => (
                    <Product key={item.id} images={images} title={item.title} price={item.price} />
                ))
            ) : (
                <p>Нет продуктов для отображения</p>
            )}
        </div>
    );
}

export default Main;

