import React, { useState, useEffect } from 'react';
import './Main.css';
import Product from '../components/Product';
import images from '../images/acrap.jpg';

function Main() {
  const [products, setProducts] = useState([]); // Изначально массив пустой
  const [loading, setLoading] = useState(true); // Флаг загрузки
  const [error, setError] = useState(null); // Для обработки ошибок

  useEffect(() => {
    const api = 'http://localhost:9001/products';

    fetch(api)
      .then((result) => {
        if (!result.ok) {
          throw new Error('Network response was not ok');
        }
        return result.json();
      })
      .then((data) => {
        console.log(data);
        
        // Проверка на наличие данных в правильной структуре
        setProducts(data.data || []); // Устанавливаем пустой массив, если data отсутствует
      })
      .catch((error) => {
        console.error('There was a problem with the fetch operation:', error);
        setError(error.message);
      })
      .finally(() => {
        setLoading(false); // Устанавливаем загрузку в false после завершения запроса
      });
  }, []); // Пустой массив зависимостей, чтобы эффект исполнялся только один раз

  if (loading) return <div>Загрузка...</div>; // Сообщение о загрузке
  if (error) return <div>Ошибка: {error}</div>; // Сообщение об ошибке

  return (
    <div className="Main">
      {products.map((item) => (
        <Product key={item.id} images={images} title={item.title} price={item.price} />
      ))}
    </div>
  );
}

export default Main;
