import React, { useState } from 'react';
import './App.css';
import Header from './components/Header';
import Footer from './components/Footer';
import Main from './views/Main';
import Basket from './views/Basket';
import ModalBox from './components/ModalBox';

function App() {
  const [page, setPage] = useState('Main');
  const [modalBoxState, setModalBoxState] = useState('none');

  const pages = {
    Main: <Main />,
    Basket: <Basket />
  };

  const ModalBoxes = {
    none: null,
    login: <ModalBox type="login" setModalBox={setModalBoxState} />,
    registration: <ModalBox type="registration" setModalBox={setModalBoxState} />
  };

  return (
    <div className="App">
      <Header setPage={setPage} setModalBox={setModalBoxState} />
      {pages[page]}
      {ModalBoxes[modalBoxState]}
      <Footer />
    </div>
  );
}

export default App;
