import React, {useState, useEffect} from 'react';
import './Main.css';
import Product from '../components/Product';
import images from '../images/acrap.jpg'

function Main() {

  const[products, setProducts] = useState([])

  useEffect(() => {

    const api = 'http://localhost:9001/products'

    fetch(api)
    .then(resulst => resulst.json())
    .then((resulst) => {
      console.log(resulst);
      setProducts(resulst.data)
    })

  }, [])

  // return (
  //   <div className="Main">
  //     { Array.isArray(products) && products.map((item) => (
  //       <Product key={item.id} header={item.header} images={images} price={item.price} />
  //     ))}
  //   </div>
  // );
  return (
    <div className="Main">
        {products.length === 0 ? (
            <p>Загрузка продуктов...</p>
        ) : (
            Array.isArray(products) && products.map((item) => (
                <Product key={item.id} header={item.header} images={images} price={item.price} />
            ))
        )}
    </div>
);
}

export default Main;
