import React, { useEffect, useState } from 'react';
import Product from './Product'; // Предполагаем, что компонент Product существует

const ProductList = () => {
    const [products, setProducts] = useState([]);
    const [images, setImages] = useState([]); // если у вас есть изображения

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch('ваш_API_URL');
                const data = await response.json();
                setProducts(data.message); // Извлечение массива из объекта
            } catch (error) {
                console.error('Ошибка при загрузке данных:', error);
            }
        };

        fetchData();
    }, []);

    return (
        <div className="Main">
            {products.map((item) => (
                <Product key={item.id} images={images} title={item.header} price={item.price} />
            ))}
        </div>
    );
};

export default ProductList;
