import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
import App from './App';
const express = require('express');
const path = require('path');

const app = express();
const PORT = 3000;

// Установка статической папки для обслуживания файлов (например, для React)
app.use(express.static(path.join(__dirname, 'build'))); // Замените 'build' на ваш каталог с фронтендом, если он отличается

// Обработчик корневого маршрута
app.get('/', (req, res) => {
  res.sendFile(path.join(__dirname, 'build', 'index.html')); // Замените путь на свой
});

// Запускаем сервер
app.listen(PORT, () => {
  console.log(`Сервер запущен на http://localhost:${PORT}`);
});

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);
