import React, { useState, useEffect } from 'react';
import './Main.css';
import Product from '../components/Product';
import images from '../images/acrap.jpg';

function Main() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const api = 'http://localhost:9001/products';

    fetch(api)
      .then(results => {
        if (!results.ok) {
          throw new Error('Network response was not ok');
        }
        return results.json();
      })
      .then((results) => {
        console.log(results);
        setProducts(results.data || []);  // Добавляем дефолтное значение
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, []);

  return (
    <div className="Main">
      {products.length > 0 ? (
        products.map((item) => (
          <Product key={item.id} images={images} title={item.title} price={item.price} />
        ))
      ) : (
        <p>No products available</p>  // Добавляем сообщение при отсутствии продуктов
      )}
    </div>
  );
}

export default Main;
