  useEffect(() => {
    const api = 'http://127.0.0.1:9001/products'

    fetch(api)
    .then((result) => result.json())
    .then((result) => {
      console.log(result.data)
      setProducts(result.data)
    })
  }, [])

  return (
    <div className="Main">
