import React, {useState, useEffect} from 'react';
import './Main.css';
import Product from '../components/Product';
import images from '../images/acrap.jpg';

function Main() {

  const [products, setProducts] = useState([]);

  const api = 'http://localhost:9001/products';
  
  // useEffect(() => {

  //   fetch(api)
  //   .then(result => result.json())
  //   .then((result) => {
  //     console.log(result);
  //     setProducts(result.data)
  //   })
  // }, [])
  useEffect(() => {
    const fetchData = async () => {
      const response = await fetch('/api/products');
      const data = await response.json();
      setProducts(data); // Убедитесь, что data — это массив
    };
  
    fetchData();
  }, []);

  return (
    <div className="Main">
      {Array.isArray(products) ? (
        products.map((item) => (
          <Product key={item.id} images={images} title={item.title} price={item.price} />
        ))
      ) : (
        <p>Нет продуктов для отображения</p>
      )}
    </div>
  );
}
export default Main;
