import React, { useState, useEffect } from 'react';
import './Main.css';
import Product from '../components/Product';
import images from '../images/acrap.jpg';

function Main() {
  
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const api = 'http://localhost:9001/products';

    fetch(api)
        .then(response => {
            if (!response.ok) {
                throw new Error('Сеть ответила с ошибкой');
            }
            return response.json();
        })
        .then(result => {
            console.log("Ответ сервера:", result);  // Логируем ответ
            if (!result || typeof result !== 'object') {
                console.error("Ответ не является объектом:", result);
            } else if (!Array.isArray(result.data)) {
                console.error("Ожидалось, что result.data будет массивом. Получено:", result.data);
            } else {
                setProducts(result.data);  // Присваиваем данные
            }
        })
        .catch(error => {
            console.error("Произошла ошибка при получении данных:", error);
        });
}, []);

  return (
    <div className="Main">
      {Array.isArray(products) && products.length > 0 ? (
        products.map(item => (
          <Product key={item.id} images={images} title={item.title} price={item.price} />
        ))
      ) : (
        <p>No products available.</p>
      )}
    </div>
  );
}

export default Main;
















