import React, { useState, useEffect } from "react";
import axios from "axios";
import Product from "./Product";

const Main = () => {
    const [products, setProducts] = useState([]);

    const fetchProducts = async () => {
        try {
            const result = await axios.get("http://localhost:9001/products");
            setProducts(result.data); // Убедитесь, что здесь правильная структура
        } catch (error) {
            console.error("Ошибка при получении продуктов", error);
        }
    };

    useEffect(() => {
        fetchProducts();
    }, []);

    return (
        <div>
            <h1>Продукты</h1>
            <div className="product-list">
                {products.map((product) => (
                    <Product
                        key={product.id}
                        images={product.image} // Убедитесь, что поле называется image или актуальное
                        title={product.title}
                        price={product.price}
                    />
                ))}
            </div>
        </div>
    );
};

export default Main;

