import React, { useState, useEffect } from 'react';
import './Main.css';
import Product from '../components/Product';
import images from '../images/acrap.jpg';

function Main() {
  
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const api = 'http://localhost:9001/products';
  
    fetch(api)
      .then(response => {
        if (!response.ok) {
          throw new Error('Сеть ответила с ошибкой');
        }
        return response.json();
      })
      .then(result => {
        console.log(result); // Посмотреть ответ сервера
        // Проверяем, существует ли `data` и является ли она массивом
        if (result && Array.isArray(result.data)) {
          setProducts(result.data);
        } else {
          console.error('Ожидалось, что result.data будет массивом. Получено:', result);
          setProducts([]); // Или обработайте ошибку по-другому
        }
      })
      .catch(error => {
        console.error('Ошибка при получении продуктов:', error);
        setProducts([]); // Или обработайте ошибку по-другому
      });
  }, []);

  return (
    <div className="Main">
      {Array.isArray(products) && products.length > 0 ? (
        products.map(item => (
          <Product key={item.id} images={images} title={item.title} price={item.price} />
        ))
      ) : (
        <p>No products available.</p>
      )}
    </div>
  );
}

export default Main;
















