// export default Main;
import React, { useState, useEffect } from 'react';
import './Main.css';
import Product from '../components/Product';
import images from '../images/acrap.jpg';

function Main() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true); // Состояние загрузки

  useEffect(() => {
    const api = 'http://localhost:9001/products';

    fetch(api)
      .then(response => {
        if (!response.ok) { // Проверка на ошибки ответа
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        // Проверяем, что data существует и является массивом
        if (data && Array.isArray(data)) {
          setProducts(data);
        } else {
          console.error('Unexpected data format:', data);
        }
      })
      .catch(error => {
        console.error('Fetch error:', error);
      })
      .finally(() => {
        setLoading(false); // Устанавливаем состояние загрузки в false после завершения
      });
  }, []);

  // Если данные все еще загружаются, показываем индикатор загрузки
  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div className="Main">
      {products.map(item => (
        <Product key={item.id} header={item.header} images={images} price={item.price} />
      ))}
    </div>
  );
}

export default Main;
